import './App.css';
import './key';
import Axios from "axios";
import { useState } from 'react';
import RecipeTile from './RecipeTile';

function App() {
  const [query, setquery] = useState("");
  const [recipes, setrecipes] = useState([]);
  const [healthLabel, sethealthLabel] = useState("vegan")

  const YOUR_APP_ID = "edae74df" ;
  const YOUR_APP_KEY = "65acfb0915dc0560612eeaddb0901ab3";

  var url = `https://api.edamam.com/search?q=${query}&app_id=${YOUR_APP_ID}&app_key=${YOUR_APP_KEY}&&health=${healthLabel}`;

  async function getRecipes() {
    var result = await Axios.get(url);
    setrecipes(result.data.hits)
    console.log(result.data);
  }

  const onSubmit = (e) => {
    e.preventDefault();
    getRecipes();
  };

  return (
    <div className="app">
      <h1>Food Recipe House</h1>
      <form className="app__searchForm" onSubmit ={onSubmit}>
        <input type="text" className="app__input" placeholder="Enter Ingredient" value={query} onChange= {(e) => setquery (e.target.value)} />
        <input className="app__submit" type="submit" value="Search" />

      <select className="app_healthLabels">
          <option value=""></option>
      </select>

      </form>

      <div className="app__recipes">
          {recipes.map((recipe) => {
            return <RecipeTile recipe={recipe}/> ;
          })}
      </div>
    </div>
  );
}

export default App;
